    return (
      <section className="container">
        {!!searchValue && (
            <>
            <h1>Search value: {searchValue}</h1>
            </>
        )}        

        <input 
          onChange = {this.handleChange}
          value = { searchValue }        
          type= "search" 

          /> <br/><br/>

        <Posts posts={posts} />

        <div className="button-container">
        {!searchValue && (
            <Button 
            text= "Load more Posts"
            onClick={this.loadMorePosts}
            disabled={noMorePosts}
            />
        )}        
        </div>

      </section>
    );
  }
}
export default App;
